import React, { useEffect, useState } from 'react';

interface Waypoint {
    _id: string;
    lat: number;
    lng: number;
    title?: string;
    description?: string;
    image?: string;
    journalText?: string;
    images?: string[];
  }
  

const ViewWaypointsPage = () => {
const [waypoints, setWaypoints] = useState([]);

useEffect(() => {
    fetch('http://localhost:3001/api/waypoints')
      .then(res => res.json())
      .then(data => setWaypoints(data))
      .catch(err => console.error('Error fetching waypoints:', err));
}, []);

return (
    <div style={{ padding: '2rem' }}>
      <h1>All Waypoints</h1>
      <div style={{ display: 'grid', gap: '1.5rem' }}>
        {waypoints.map(wp => (
          <div key={wp._id} style={{ border: '1px solid #ccc', padding: '1rem', borderRadius: '8px' }}>
            <h2>{wp.title}</h2>
            <p><strong>Description:</strong> {wp.description}</p>
            <p><strong>Latitude:</strong> {wp.lat}</p>
            <p><strong>Longitude:</strong> {wp.lng}</p>
            {wp.image && (
              <img
                src={`http://localhost:3001${wp.image}`}
                alt={wp.title}
                style={{ maxWidth: '300px', borderRadius: '8px' }}
              />
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewWaypointsPage;
