import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import styles from './JournalPage.module.css'; // Import CSS Module

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  description?: string;
  journalText?: string;
  images?: string[];
}

export default function JournalPage() {
  const { id } = useParams();
  const [waypoint, setWaypoint] = useState<Waypoint | null>(null);
  const [journalText, setJournalText] = useState('');
  const [images, setImages] = useState<string[]>([]);
  const [files, setFiles] = useState<File[]>([]); // To store selected files
  const [imagePreviews, setImagePreviews] = useState<string[]>([]); // To store preview URLs
  const [uploading, setUploading] = useState(false);
  const [zoomedImage, setZoomedImage] = useState<string | null>(null);

  useEffect(() => {
    axios.get(`/api/waypoints`).then(res => {
      const wp = res.data.find((w: Waypoint) => w._id === id);
      if (wp) {
        setWaypoint(wp);
        setJournalText(wp.journalText || '');
        setImages(wp.images || []);
      }
    });
  }, [id]);

  const handleSave = () => {
    axios.put(`/api/waypoints/${id}`, {
      journalText,
      images
    }).then(() => {
      alert('Saved!');
    });
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFiles = e.target.files ? Array.from(e.target.files) : [];
    setFiles(prevFiles => [...prevFiles, ...selectedFiles]); // Append selected files

    // Generate image previews
    const previewUrls = selectedFiles.map(file => URL.createObjectURL(file));
    setImagePreviews(prevPreviews => [...prevPreviews, ...previewUrls]); // Append previews
  };

  const handleFileUpload = async () => {
    if (!files.length) return;

    const formData = new FormData();
    files.forEach(file => formData.append('images', file)); // Appending multiple files

    setUploading(true);
    try {
      const res = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      // Assuming the server returns the uploaded image URLs in res.data.urls
      const uploadedImages = res.data.urls;

      // Add uploaded images to the existing list of images
      setImages(prev => [...prev, ...uploadedImages]);

      // Clear the files and preview state after uploading
      setFiles([]); 
      setImagePreviews([]); 
    } catch (err: any) {
      console.error('Upload error:', err);
      alert('Upload failed: ' + (err.response?.data?.message || err.message || 'Unknown error'));
    } finally {
      setUploading(false);
    }
  };

  const handleImageDelete = async (imageUrl: string, index: number) => {
    try {
      // Send a request to the server to delete the image from the server's storage
      await axios.delete(`/api/delete-image`, { data: { imageUrl } });

      // Remove the image from the local state (journal)
      setImages(prev => prev.filter((_, i) => i !== index));
    } catch (err: any) {
      alert('Failed to delete the image.');
      console.error('Error deleting image:', err);
    }
  };

  if (!waypoint) return <p>Loading...</p>;

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>
        Journal for Location [{waypoint.lat.toFixed(2)}, {waypoint.lng.toFixed(2)}]
      </h2>

      <textarea
        className={styles.textarea}
        value={journalText}
        onChange={e => setJournalText(e.target.value)}
        placeholder="Write your journal entry here..."
      />

      <h4>Uploaded Images:</h4>
      <div className={styles.uploadedImages}>
        {images.map((img, i) => (
          <div key={i} className={styles.imageWrapper}>
            <img
              src={img}
              alt={`upload-${i}`}
              className={styles.image}
              onClick={() => setZoomedImage(img)}
            />
            <button
              onClick={() => handleImageDelete(img, i)}
              className={styles.deleteButton}
            >
              Ã—
            </button>
          </div>
        ))}
      </div>

      <h4>Preview Images (Before Uploading):</h4>
      <div className={styles.uploadedImages}>
        {imagePreviews.map((preview, i) => (
          <div key={i} className={styles.imageWrapper}>
            <img
              src={preview}
              alt={`preview-${i}`}
              className={styles.image}
            />
          </div>
        ))}
      </div>

      <div className={styles.fileInputWrapper}>
        <input
          type="file"
          onChange={handleFileChange}
          disabled={uploading}
          multiple // Allow multiple file selection
          className={styles.fileInput}
        />
        <button onClick={handleFileUpload} disabled={!files.length || uploading} className={styles.uploadButton}>
          {uploading ? 'Uploading...' : 'Upload Images'}
        </button>
      </div>

      <button className={styles.uploadButton} onClick={handleSave}>
        Save Journal
      </button>

      {/* Zoom Modal */}
      {zoomedImage && (
        <div
          onClick={() => setZoomedImage(null)}
          className={styles.zoomModal}
        >
          <img
            src={zoomedImage}
            alt="Zoomed"
            className={styles.zoomedImage}
          />
        </div>
      )}
    </div>
  );
}
