import { MapContainer, TileLayer, Marker, Popup, useMapEvents, Tooltip } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L, { LatLngExpression, LeafletMouseEvent, Icon } from 'leaflet';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  title?: string;
  description?: string;
  image?: string;
  journalText?: string;
  images?: string[];
}

// Custom waypoint icon
const waypointIcon = new Icon({
  iconUrl: '/point-of-interest.png', // You need this asset in your public folder or update the path
  iconSize: [30, 30],
  iconAnchor: [15, 30],
  popupAnchor: [0, -30],
});

export default function MapPage() {
  const [waypoints, setWaypoints] = useState<Waypoint[]>([]);

  useEffect(() => {
    axios.get('/api/waypoints').then(res => setWaypoints(res.data));
  }, []);

  const AddWaypoint = () => {
    useMapEvents({
      click: async (e: LeafletMouseEvent) => {
        const { lat, lng } = e.latlng;
        const title = prompt('Optional: Enter a title for this location');
        const description = prompt('Optional: Enter a description');

        const imageFile = window.confirm('Do you want to upload an image for this location?') 
          ? await new Promise<File | null>((resolve) => {
              const input = document.createElement('input');
              input.type = 'file';
              input.accept = 'image/*';
              input.onchange = () => resolve(input.files?.[0] ?? null);
              input.click();
            })
          : null;

        let imageUrl;
        if (imageFile) {
          const formData = new FormData();
          formData.append('images', imageFile);
          const res = await axios.post('/api/upload', formData, {
            headers: { 'Content-Type': 'multipart/form-data' },
          });
          imageUrl = res.data.urls?.[0]; // Assuming your API responds with { urls: [...] }
        }

        const newWaypoint = {
          lat,
          lng,
          title,
          description,
          image: imageUrl,
        };

        axios.post('/api/waypoints', newWaypoint).then(res => {
          setWaypoints(prev => [...prev, res.data]);
        });
      }
    });
    return null;
  };

  const center: LatLngExpression = [20, 0];

  return (
    <MapContainer
      center={center}
      zoom={2}
      style={{ height: '100vh' }}
      worldCopyJump={false}
      maxBounds={[[-90, -180], [90, 180]]}
      maxBoundsViscosity={1.0}
    >
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      <AddWaypoint />
      {waypoints.map(wp => (
        <Marker
          key={wp._id}
          position={[wp.lat, wp.lng]}
          icon={waypointIcon}
          eventHandlers={{
            contextmenu: () => {
              // eslint-disable-next-line no-restricted-globals
              if (confirm('Delete this waypoint?')) {
                axios.delete(`/api/waypoints/${wp._id}`).then(() => {
                  setWaypoints(prev => prev.filter(p => p._id !== wp._id));
                });
              }
            },
          }}
        >
          <Tooltip>{wp.title || wp.description || 'Untitled Waypoint'}</Tooltip>
          <Popup>
            <div>
              {wp.image && <img src={wp.image} width="100" alt="Waypoint" />}
              <h4>{wp.title}</h4>
              <p>{wp.description}</p>
              <a href={`/journal/${wp._id}`}>View Journal</a>
            </div>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}
