import { MapContainer, TileLayer, Marker, Popup, useMapEvents, Tooltip } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L, { LatLngExpression, LeafletMouseEvent, Icon } from 'leaflet';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  title?: string;
  description?: string;
  image?: string;
  journalText?: string;
  images?: string[];
}

// Custom waypoint icon
const waypointIcon = new Icon({
  iconUrl: '/point-of-interest.png',
  iconSize: [30, 30],
  iconAnchor: [15, 30],
  popupAnchor: [0, -30],
});

export default function MapPage() {
  const [waypoints, setWaypoints] = useState<Waypoint[]>([]);
  const [newWaypoint, setNewWaypoint] = useState<{ lat: number; lng: number } | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState<{ title: string; description: string; imageFile: File | null }>({
    title: '',
    description: '',
    imageFile: null,
  });

  useEffect(() => {
    axios.get('/api/waypoints').then(res => setWaypoints(res.data));
  }, []);

  const AddWaypoint = () => {
    useMapEvents({
      click: (e: LeafletMouseEvent) => {
        setNewWaypoint({ lat: e.latlng.lat, lng: e.latlng.lng });
        setShowModal(true);
      },
    });
    return null;
  };

  const handleFormSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newWaypoint) return;

    let imageUrl;
    if (formData.imageFile) {
      const uploadData = new FormData();
      uploadData.append('images', formData.imageFile);
      const res = await axios.post('/api/upload', uploadData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      imageUrl = res.data.urls?.[0];
    }

    const newWp = {
      ...newWaypoint,
      title: formData.title,
      description: formData.description,
      image: imageUrl,
    };

    const res = await axios.post('/api/waypoints', newWp);
    setWaypoints(prev => [...prev, res.data]);
    setShowModal(false);
    setFormData({ title: '', description: '', imageFile: null });
    setNewWaypoint(null);
  };

  const center: LatLngExpression = [20, 0];

  return (
    <>
      <MapContainer
        center={center}
        zoom={2}
        style={{ height: '100vh' }}
        worldCopyJump={false}
        maxBounds={[[-90, -180], [90, 180]]}
        maxBoundsViscosity={1.0}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          noWrap={true}
        />
        <TileLayer
          url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png"
          attribution='&copy; <a href="https://carto.com/">CARTO</a>'
          noWrap={true}
        />
        <AddWaypoint />
        {waypoints.map(wp => (
          <Marker
            key={wp._id}
            position={[wp.lat, wp.lng]}
            icon={waypointIcon}
            eventHandlers={{
              contextmenu: () => {
                if (confirm('Delete this waypoint?')) {
                  axios.delete(`/api/waypoints/${wp._id}`).then(() => {
                    setWaypoints(prev => prev.filter(p => p._id !== wp._id));
                  });
                }
              },
            }}
          >
            <Tooltip>{wp.title || wp.description || 'Untitled Waypoint'}</Tooltip>
            <Popup>
              <div>
                {wp.image && <img src={wp.image} width="100" alt="Waypoint" />}
                <h4>{wp.title}</h4>
                <p>{wp.description}</p>
                <a href={`/journal/${wp._id}`}>View Journal</a>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {showModal && (
        <div className="modal" style={{ position: 'fixed', top: 50, left: '50%', transform: 'translateX(-50%)', backgroundColor: '#fff', padding: '1em', borderRadius: '10px', zIndex: 1000 }}>
          <h3>Add New Waypoint</h3>
          <form onSubmit={handleFormSubmit}>
            <input
              type="text"
              placeholder="Title"
              value={formData.title}
              onChange={e => setFormData({ ...formData, title: e.target.value })}
              required
            /><br />
            <textarea
              placeholder="Description"
              value={formData.description}
              onChange={e => setFormData({ ...formData, description: e.target.value })}
              required
            /><br />
            <input
              type="file"
              accept="image/*"
              onChange={e => setFormData({ ...formData, imageFile: e.target.files?.[0] || null })}
            /><br />
            <button type="submit">Add Waypoint</button>
            <button type="button" onClick={() => setShowModal(false)}>Cancel</button>
          </form>
        </div>
      )}
    </>
  );
}
