import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  title: string;
  description: string;
  image?: string;
  images?: string[];
}

const ViewWaypointsPage = () => {
  const [waypoints, setWaypoints] = useState<Waypoint[]>([]);

  useEffect(() => {
    fetch('http://localhost:3001/api/waypoints')
      .then(res => res.json())
      .then(data => setWaypoints(data))
      .catch(err => console.error('Error fetching waypoints:', err));
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>All Waypoints</h1>
      <div style={{ display: 'grid', gap: '1.5rem' }}>
        {waypoints.map(wp => (
          <div
            key={wp._id}
            style={{
              border: '1px solid #ccc',
              padding: '1rem',
              borderRadius: '8px',
              boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
            }}
          >
            <h2>{wp.title}</h2>
            <p><strong>Description:</strong> {wp.description}</p>
            <p><strong>Latitude:</strong> {wp.lat}</p>
            <p><strong>Longitude:</strong> {wp.lng}</p>
            {wp.image && (
              <img
                src={`http://localhost:3001${wp.image}`}
                alt={wp.title}
                style={{ maxWidth: '300px', borderRadius: '8px', marginTop: '1rem' }}
              />
            )}
            <div style={{ marginTop: '1rem' }}>
              <Link
                to={`/journal/${wp._id}`}
                style={{
                  display: 'inline-block',
                  backgroundColor: '#007bff',
                  color: '#fff',
                  padding: '0.5rem 1rem',
                  borderRadius: '4px',
                  textDecoration: 'none'
                }}
              >
                View Journal
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ViewWaypointsPage;
