import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  description?: string;
  journalText?: string;
  images?: string[];
}

export default function JournalPage() {
  const { id } = useParams();
  const [waypoint, setWaypoint] = useState<Waypoint | null>(null);
  const [journalText, setJournalText] = useState('');
  const [newImage, setNewImage] = useState('');
  const [images, setImages] = useState<string[]>([]);

  useEffect(() => {
    axios.get(`/api/waypoints`).then(res => {
      const wp = res.data.find((w: Waypoint) => w._id === id);
      if (wp) {
        setWaypoint(wp);
        setJournalText(wp.journalText || '');
        setImages(wp.images || []);
      }
    });
  }, [id]);

  const handleSave = () => {
    axios.put(`/api/waypoints/${id}`, {
      journalText,
      images
    }).then(res => {
      alert('Saved!');
    });
  };

  const handleImageUpload = () => {
    if (newImage) {
      setImages(prev => [...prev, newImage]);
      setNewImage('');
    }
  };

  if (!waypoint) return <p>Loading...</p>;

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Journal for Location [{waypoint.lat.toFixed(2)}, {waypoint.lng.toFixed(2)}]</h2>

      <textarea
        style={{ width: '100%', height: '150px', marginTop: '1rem' }}
        value={journalText}
        onChange={e => setJournalText(e.target.value)}
        placeholder="Write your journal entry here..."
      />

      <h4>Uploaded Images:</h4>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        {images.map((img, i) => (
          <img key={i} src={img} alt={`upload-${i}`} width="150" />
        ))}
      </div>

      <div style={{ marginTop: '1rem' }}>
        <input
          type="text"
          value={newImage}
          onChange={e => setNewImage(e.target.value)}
          placeholder="Paste image URL here"
        />
        <button onClick={handleImageUpload}>Add Image</button>
      </div>

      <button style={{ marginTop: '1rem' }} onClick={handleSave}>
        Save Journal
      </button>
    </div>
  );
}
