import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  description?: string;
  journalText?: string;
  images?: string[];
}

export default function JournalPage() {
  const { id } = useParams();
  const [waypoint, setWaypoint] = useState<Waypoint | null>(null);
  const [journalText, setJournalText] = useState('');
  const [images, setImages] = useState<string[]>([]);
  const [files, setFiles] = useState<File[]>([]); // Updated to support multiple files
  const [uploading, setUploading] = useState(false);
  const [zoomedImage, setZoomedImage] = useState<string | null>(null);

  useEffect(() => {
    axios.get(`/api/waypoints`).then(res => {
      const wp = res.data.find((w: Waypoint) => w._id === id);
      if (wp) {
        setWaypoint(wp);
        setJournalText(wp.journalText || '');
        setImages(wp.images || []);
      }
    });
  }, [id]);

  const handleSave = () => {
    axios.put(`/api/waypoints/${id}`, {
      journalText,
      images
    }).then(() => {
      alert('Saved!');
    });
  };

  const handleFileUpload = async () => {
    if (!files.length) return;

    const formData = new FormData();
    files.forEach(file => formData.append('images', file)); // Appending multiple files

    setUploading(true);
    try {
      const res = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      // Add all uploaded images to the existing list of images
      setImages(prev => [...prev, ...res.data.urls]); // Assuming the server returns an array of URLs
      setFiles([]); // Reset file input
    } catch (err: any) {
      console.error('Upload error:', err);
      alert('Upload failed: ' + (err.response?.data?.message || err.message || 'Unknown error'));
    } finally {
      setUploading(false);
    }
  };

  const handleImageDelete = async (imageUrl: string, index: number) => {
    try {
      // Send a request to the server to delete the image from the server's storage
      await axios.delete(`/api/delete-image`, { data: { imageUrl } });
  
      // Remove the image from the local state (journal)
      setImages(prev => prev.filter((_, i) => i !== index));
    } catch (err: any) {
      alert('Failed to delete the image.');
      console.error('Error deleting image:', err);
    }
  };

  if (!waypoint) return <p>Loading...</p>;

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Journal for Location [{waypoint.lat.toFixed(2)}, {waypoint.lng.toFixed(2)}]</h2>

      <textarea
        style={{ width: '100%', height: '150px', marginTop: '1rem' }}
        value={journalText}
        onChange={e => setJournalText(e.target.value)}
        placeholder="Write your journal entry here..."
      />

      <h4>Uploaded Images:</h4>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        {images.map((img, i) => (
            <div key={i} style={{ position: 'relative', display: 'inline-block' }}>
            <img
                src={img}
                alt={`upload-${i}`}
                width="150"
                style={{ cursor: 'pointer' }}
                onClick={() => setZoomedImage(img)}
            />
            <button
                onClick={() => handleImageDelete(img, i)}  // Call the delete handler
                style={{
                position: 'absolute',
                top: 0,
                right: 0,
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                cursor: 'pointer',
                width: '24px',
                height: '24px',
                fontWeight: 'bold'
                }}
            >
                ×
            </button>
            </div>
        ))}
    </div>

      <div style={{ marginTop: '1rem' }}>
        <input
          type="file"
          onChange={e => setFiles(Array.from(e.target.files || []))}
          disabled={uploading}
          multiple // Allow multiple file selection
        />
        <button onClick={handleFileUpload} disabled={!files.length || uploading}>
          {uploading ? 'Uploading...' : 'Upload Images'}
        </button>
      </div>

      <button style={{ marginTop: '1rem' }} onClick={handleSave}>
        Save Journal
      </button>

      {/* Zoom Modal */}
      {zoomedImage && (
        <div
          onClick={() => setZoomedImage(null)}
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0,0,0,0.8)',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            zIndex: 9999,
            cursor: 'zoom-out'
          }}
        >
          <img
            src={zoomedImage}
            alt="Zoomed"
            style={{
              maxHeight: '90%',
              maxWidth: '90%',
              borderRadius: '8px',
              boxShadow: '0 0 20px black'
            }}
          />
        </div>
      )}
    </div>
  );
}
