import { MapContainer, TileLayer, Marker, Popup, useMapEvents, Tooltip } from 'react-leaflet';
import { useEffect, useState, Fragment } from 'react';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L, { LatLngExpression, LeafletMouseEvent, Icon } from 'leaflet';
import { Dialog } from '@headlessui/react';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  title?: string;
  description?: string;
  image?: string;
}

const waypointIcon = new Icon({
  iconUrl: '/point-of-interest.png',
  iconSize: [30, 30],
  iconAnchor: [15, 30],
  popupAnchor: [0, -30],
});

export default function MapPage() {
  const [waypoints, setWaypoints] = useState<Waypoint[]>([]);
  const [editingWaypoint, setEditingWaypoint] = useState<Waypoint | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [editFields, setEditFields] = useState({ title: '', description: '', image: '' });

  useEffect(() => {
    axios.get('/api/waypoints').then(res => setWaypoints(res.data));
  }, []);

  const AddWaypoint = () => {
    useMapEvents({
      click: async (e: LeafletMouseEvent) => {
        const { lat, lng } = e.latlng;
        const res = await axios.post('/api/waypoints', { lat, lng });
        setWaypoints(prev => [...prev, res.data]);
      },
    });
    return null;
  };

  const openEditModal = (wp: Waypoint) => {
    setEditingWaypoint(wp);
    setEditFields({
      title: wp.title || '',
      description: wp.description || '',
      image: wp.image || '',
    });
    setShowModal(true);
  };

  const handleEditSubmit = async () => {
    if (!editingWaypoint) return;

    const updatedWaypoint = {
      ...editingWaypoint,
      ...editFields,
    };

    const res = await axios.put(`/api/waypoints/${editingWaypoint._id}`, updatedWaypoint);
    setWaypoints(prev =>
      prev.map(w => (w._id === editingWaypoint._id ? res.data : w))
    );

    setShowModal(false);
    setEditingWaypoint(null);
  };

  const handleImageUpload = async (file: File) => {
    const formData = new FormData();
    formData.append('images', file);
    const res = await axios.post('/api/upload', formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    setEditFields(prev => ({ ...prev, image: res.data.urls?.[0] || '' }));
  };

  const center: LatLngExpression = [20, 0];

  return (
    <>
      <MapContainer
        center={center}
        zoom={2}
        style={{ height: '100vh' }}
        worldCopyJump={false}
        maxBounds={[[-90, -180], [90, 180]]}
        maxBoundsViscosity={1.0}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <AddWaypoint />
        {waypoints.map(wp => (
          <Marker
            key={wp._id}
            position={[wp.lat, wp.lng]}
            icon={waypointIcon}
            eventHandlers={{
              contextmenu: () => {
                // eslint-disable-next-line no-restricted-globals
                if (confirm('Delete this waypoint?')) {
                  axios.delete(`/api/waypoints/${wp._id}`).then(() => {
                    setWaypoints(prev => prev.filter(p => p._id !== wp._id));
                  });
                }
              },
            }}
          >
            <Tooltip>{wp.title || wp.description || 'Untitled Waypoint'}</Tooltip>
            <Popup>
              <div>
                {wp.image && <img src={wp.image} width="100" alt="Waypoint" />}
                <h4>{wp.title}</h4>
                <p>{wp.description}</p>
                <div>
                  <a href={`/journal/${wp._id}`}>View Journal</a> |{' '}
                  <a
                    href="#"
                    onClick={(e) => {
                      e.preventDefault();
                      openEditModal(wp);
                    }}
                  >
                    Edit Waypoint
                  </a>
                </div>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {showModal && (
        <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-[90%] max-w-md">
            <h2 className="text-lg font-semibold mb-4">Edit Waypoint</h2>
            <input
                className="w-full p-2 border rounded mb-2"
                placeholder="Title"
                value={editFields.title}
                onChange={e => setEditFields({ ...editFields, title: e.target.value })}
            />
            <textarea
                className="w-full p-2 border rounded mb-2"
                placeholder="Description"
                value={editFields.description}
                onChange={e => setEditFields({ ...editFields, description: e.target.value })}
            />
            <input
                type="file"
                accept="image/*"
                onChange={e => {
                const file = e.target.files?.[0];
                if (file) handleImageUpload(file);
                }}
            />
            {editFields.image && (
                <img src={editFields.image} alt="Thumbnail" className="mt-2 w-24 rounded" />
            )}
            <div className="flex justify-end gap-2 mt-4">
                <button
                onClick={() => setShowModal(false)}
                className="px-4 py-2 bg-gray-300 rounded hover:bg-gray-400"
                >
                Cancel
                </button>
                <button
                onClick={handleEditSubmit}
                className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                >
                Save
                </button>
            </div>
            </div>
        </div>
      )}


      <Dialog open={showModal} onClose={() => setShowModal(false)} className="relative z-50">
        <div className="fixed inset-0 bg-black/30 flex items-center justify-center">
          <Dialog.Panel className="bg-white p-6 rounded-lg shadow-lg w-[90%] max-w-md">
            <Dialog.Title className="text-lg font-semibold mb-4">Edit Waypoint</Dialog.Title>
            <input
              className="w-full p-2 border rounded mb-2"
              placeholder="Title"
              value={editFields.title}
              onChange={e => setEditFields({ ...editFields, title: e.target.value })}
            />
            <textarea
              className="w-full p-2 border rounded mb-2"
              placeholder="Description"
              value={editFields.description}
              onChange={e => setEditFields({ ...editFields, description: e.target.value })}
            />
            <div className="mb-2">
              <input
                type="file"
                accept="image/*"
                onChange={e => {
                  const file = e.target.files?.[0];
                  if (file) handleImageUpload(file);
                }}
              />
              {editFields.image && (
                <img src={editFields.image} alt="Thumbnail" className="mt-2 w-24 rounded" />
              )}
            </div>
            <button
              onClick={handleEditSubmit}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Save Changes
            </button>
          </Dialog.Panel>
        </div>
      </Dialog>
    </>
  );
}
