import { MapContainer, TileLayer, Marker, Popup, useMapEvents, Tooltip } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import L, { LatLngExpression, LeafletMouseEvent, Icon } from 'leaflet';
import { Dialog } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  title?: string;
  description?: string;
  image?: string;
  journalText?: string;
  images?: string[];
}

const waypointIcon = new Icon({
  iconUrl: '/point-of-interest.png',
  iconSize: [30, 30],
  iconAnchor: [15, 30],
  popupAnchor: [0, -30],
});

export default function MapPage() {
  const [waypoints, setWaypoints] = useState<Waypoint[]>([]);
  const [editing, setEditing] = useState<Waypoint | null>(null);
  const [form, setForm] = useState({ title: '', description: '', image: '' });

  useEffect(() => {
    axios.get('/api/waypoints').then(res => setWaypoints(res.data));
  }, []);

  const AddWaypoint = () => {
    useMapEvents({
      click: async (e: LeafletMouseEvent) => {
        const { lat, lng } = e.latlng;
        const res = await axios.post('/api/waypoints', { lat, lng });
        setWaypoints(prev => [...prev, res.data]);
      }
    });
    return null;
  };

  const center: LatLngExpression = [20, 0];

  const handleEdit = (wp: Waypoint) => {
    setEditing(wp);
    setForm({ title: wp.title || '', description: wp.description || '', image: wp.image || '' });
  };

  const handleSave = async () => {
    if (!editing) return;
    let imageUrl = form.image;

    if (form.image && typeof form.image !== 'string') {
      const formData = new FormData();
      formData.append('images', form.image);
      const res = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      imageUrl = res.data.urls?.[0];
    }

    const updated = { ...editing, title: form.title, description: form.description, image: imageUrl };
    await axios.put(`/api/waypoints/${editing._id}`, updated);
    setWaypoints(prev => prev.map(p => (p._id === editing._id ? updated : p)));
    setEditing(null);
  };

  return (
    <>
      <MapContainer
        center={center}
        zoom={2}
        style={{ height: '100vh' }}
        worldCopyJump={false}
        maxBounds={[[-90, -180], [90, 180]]}
        maxBoundsViscosity={1.0}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <AddWaypoint />
        {waypoints.map(wp => (
          <Marker
            key={wp._id}
            position={[wp.lat, wp.lng]}
            icon={waypointIcon}
            eventHandlers={{
              contextmenu: () => {
                if (confirm('Delete this waypoint?')) {
                  axios.delete(`/api/waypoints/${wp._id}`).then(() => {
                    setWaypoints(prev => prev.filter(p => p._id !== wp._id));
                  });
                }
              },
            }}
          >
            <Tooltip>{wp.title || wp.description || 'Untitled Waypoint'}</Tooltip>
            <Popup>
              <div>
                {wp.image && <img src={wp.image} width="100" alt="Waypoint" />}
                <h4>{wp.title}</h4>
                <p>{wp.description}</p>
                <div className="flex gap-2 mt-2">
                  <a href={`/journal/${wp._id}`}><Button>View Journal</Button></a>
                  <Button onClick={() => handleEdit(wp)}>Edit Waypoint</Button>
                </div>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {editing && (
        <Dialog open={true} onOpenChange={() => setEditing(null)}>
          <div className="p-4 bg-white rounded-xl shadow-xl max-w-md mx-auto">
            <h2 className="text-xl font-bold mb-2">Edit Waypoint</h2>
            <label className="block mb-2">Title:
              <input
                type="text"
                value={form.title}
                onChange={e => setForm({ ...form, title: e.target.value })}
                className="w-full border p-2 rounded"
              />
            </label>
            <label className="block mb-2">Description:
              <textarea
                value={form.description}
                onChange={e => setForm({ ...form, description: e.target.value })}
                className="w-full border p-2 rounded"
              />
            </label>
            <label className="block mb-2">Thumbnail:
              <input
                type="file"
                accept="image/*"
                onChange={e => setForm({ ...form, image: e.target.files?.[0] || '' })}
                className="w-full border p-2 rounded"
              />
            </label>
            <div className="flex gap-4 mt-4">
              <Button onClick={handleSave}>Save</Button>
              <Button variant="outline" onClick={() => setEditing(null)}>Cancel</Button>
            </div>
          </div>
        </Dialog>
      )}
    </>
  );
}
