import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

interface Waypoint {
  _id: string;
  lat: number;
  lng: number;
  description?: string;
  journalText?: string;
  images?: string[];
}

export default function JournalPage() {
  const { id } = useParams();
  const [waypoint, setWaypoint] = useState<Waypoint | null>(null);
  const [journalText, setJournalText] = useState('');
  const [images, setImages] = useState<string[]>([]);
  const [file, setFile] = useState<File | null>(null);
  const [uploading, setUploading] = useState(false);
  const [zoomedImage, setZoomedImage] = useState<string | null>(null);


  useEffect(() => {
    axios.get(`/api/waypoints`).then(res => {
      const wp = res.data.find((w: Waypoint) => w._id === id);
      if (wp) {
        setWaypoint(wp);
        setJournalText(wp.journalText || '');
        setImages(wp.images || []);
      }
    });
  }, [id]);

  const handleSave = () => {
    axios.put(`/api/waypoints/${id}`, {
      journalText,
      images
    }).then(() => {
      alert('Saved!');
    });
  };

  const handleFileUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('image', file);

    setUploading(true);
    try {
      const res = await axios.post('/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      setImages(prev => [...prev, res.data.url]);
      setFile(null);
    } catch (err) {
      alert('Upload failed.');
    } finally {
      setUploading(false);
    }
  };

  if (!waypoint) return <p>Loading...</p>;

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Journal for Location [{waypoint.lat.toFixed(2)}, {waypoint.lng.toFixed(2)}]</h2>

      <textarea
        style={{ width: '100%', height: '150px', marginTop: '1rem' }}
        value={journalText}
        onChange={e => setJournalText(e.target.value)}
        placeholder="Write your journal entry here..."
      />

      <h4>Uploaded Images:</h4>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        {images.map((img, i) => (
            <div key={i} style={{ position: 'relative', display: 'inline-block' }}>
            <img src={img} alt={`upload-${i}`} width="150" />
            <button
                onClick={() => setImages(prev => prev.filter((_, index) => index !== i))}
                style={{
                position: 'absolute',
                top: 0,
                right: 0,
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                cursor: 'pointer',
                width: '24px',
                height: '24px',
                fontWeight: 'bold'
                }}
            >
                Ã—
            </button>
            </div>
        ))}
    </div>

      <div style={{ marginTop: '1rem' }}>
        <input type="file" onChange={e => setFile(e.target.files?.[0] || null)} />
        <button onClick={handleFileUpload} disabled={!file || uploading}>
          {uploading ? 'Uploading...' : 'Upload Image'}
        </button>
      </div>

      <button style={{ marginTop: '1rem' }} onClick={handleSave}>
        Save Journal
      </button>
    </div>
  );
}
